    const newPricePerUnit = calculatePricePerUnit(newBundlePrice, articleForm.content, isGross);
    setArticleForm(prev => ({
      ...prev,
      bundlePrice: newBundlePrice,
      isGrossPrice: isGross,
      pricePerUnit: newPricePerUnit
    }));
  };

