import React, { useState, useEffect } from 'react';
import { FaUtensils, FaBars, FaTimes, FaCalculator, FaShoppingCart, FaBoxes, FaPalette, FaPlus, FaSearch, FaCog } from 'react-icons/fa';

// Design Templates
const designTemplates = {
  modern: {
    name: "Modern & Minimal",
    description: "Clean, viel Weißraum, moderne Typografie",
    colors: {
      primary: "#2c3e50",
      secondary: "#ecf0f1",
      accent: "#3498db",
      background: "#ffffff",
      sidebar: "#f8f9fa",
      text: "#2c3e50",
      card: "#ffffff",
      cardBorder: "#e9ecef",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(44, 62, 80, 0.1)"
    }
  },
  dark: {
    name: "Dark Theme",
    description: "Dunkles Design mit Akzentfarben",
    colors: {
      primary: "#1a1a1a",
      secondary: "#2d2d2d",
      accent: "#00d4aa",
      background: "#121212",
      sidebar: "#1e1e1e",
      text: "#ffffff",
      card: "#2d2d2d",
      cardBorder: "#404040",
      paper: "#2d2d2d",
      paperShadow: "0 4px 12px rgba(0, 0, 0, 0.3)"
    }
  },
  warm: {
    name: "Warm & Gastronomisch",
    description: "Braun/Beige Töne, gemütlich",
    colors: {
      primary: "#8B4513",
      secondary: "#F5DEB3",
      accent: "#D2691E",
      background: "#FFF8DC",
      sidebar: "#DEB887",
      text: "#654321",
      card: "#FDF5E6",
      cardBorder: "#D2B48C",
      paper: "#FFFFFF",
      paperShadow: "0 4px 12px rgba(139, 69, 19, 0.15)"
    }
  },
  professional: {
    name: "Professional Blue",
    description: "Business-Look mit Blau",
    colors: {
      primary: "#1e3a8a",
      secondary: "#dbeafe",
      accent: "#3b82f6",
      background: "#f8fafc",
      sidebar: "#1e40af",
      text: "#1e293b",
      card: "#ffffff",
      cardBorder: "#e2e8f0",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(30, 58, 138, 0.1)"
    }
  }
};

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  // Debug: Log currentPage changes
  useEffect(() => {
    console.log('Current page changed to:', currentPage);
  }, [currentPage]);
  const [currentDesign, setCurrentDesign] = useState('warm');
  const [showDesignSelector, setShowDesignSelector] = useState(false);

  // Check screen size on mount and resize
  useEffect(() => {
    const checkScreenSize = () => {
      setIsMobile(window.innerWidth < 768);
      if (window.innerWidth < 768) {
        setSidebarOpen(false);
      } else {
        setSidebarOpen(true);
      }
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getCurrentColors = () => designTemplates[currentDesign as keyof typeof designTemplates].colors;

  const renderPage = () => {
    const colors = getCurrentColors();

    switch (currentPage) {
      case 'dashboard':
        return (
          <div className="container-fluid p-4">
            {/* Paper-like Dashboard */}
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h1 style={{ color: colors.text, margin: 0 }}>Dashboard</h1>
                <div className="d-flex gap-2">
                  <button className="btn btn-outline-secondary btn-sm">
                    <FaPlus className="me-1" />
                    Neuer Artikel
                  </button>
                  <button className="btn btn-outline-secondary btn-sm">
                    <FaPlus className="me-1" />
                    Neues Rezept
                  </button>
                </div>
              </div>

              <div className="row">
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        📦
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Artikel</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Verfügbare Artikel</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        👥
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Lieferanten</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Aktive Partner</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        📖
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Rezepte</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Gespeicherte Rezepte</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        💰
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Gesamtwert</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>€0</p>
                      <small style={{ color: colors.text }}>Lagerwert</small>
                    </div>
                  </div>
                </div>
              </div>

              {/* Quick Actions Section */}
              <div className="row mt-4">
                <div className="col-12">
                  <h3 style={{ color: colors.text, marginBottom: '1rem' }}>Schnellzugriff</h3>
                  <div className="row">
                    <div className="col-md-4 mb-3">
                      <button className="btn btn-outline-primary w-100 p-3" style={{ 
                        borderColor: colors.accent, 
                        color: colors.accent,
                        borderRadius: '8px'
                      }}>
                        <FaPlus className="me-2" />
                        Neuen Artikel anlegen
                      </button>
                    </div>
                    <div className="col-md-4 mb-3">
                      <button className="btn btn-outline-primary w-100 p-3" style={{ 
                        borderColor: colors.accent, 
                        color: colors.accent,
                        borderRadius: '8px'
                      }}>
                        <FaPlus className="me-2" />
                        Neues Rezept erstellen
                      </button>
                    </div>
                    <div className="col-md-4 mb-3">
                      <button className="btn btn-outline-primary w-100 p-3" style={{ 
                        borderColor: colors.accent, 
                        color: colors.accent,
                        borderRadius: '8px'
                      }}>
                        <FaSearch className="me-2" />
                        Artikel durchsuchen
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      case 'artikel':
        return (
          <div className="container-fluid p-4">
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <h1 style={{ color: colors.text }}>Artikelverwaltung</h1>
              <p style={{ color: colors.text }}>Hier können später Artikel verwaltet werden.</p>
            </div>
          </div>
        );
      case 'lieferanten':
        return (
          <div className="container-fluid p-4">
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <h1 style={{ color: colors.text }}>Lieferantenverwaltung</h1>
              <p style={{ color: colors.text }}>Hier können später Lieferanten verwaltet werden.</p>
            </div>
          </div>
        );
      case 'rezepte':
        return (
          <div className="container-fluid p-4">
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <h1 style={{ color: colors.text }}>Rezeptverwaltung</h1>
              <p style={{ color: colors.text }}>Hier können später Rezepte verwaltet werden.</p>
            </div>
          </div>
        );
      case 'einkauf':
        return (
          <div className="container-fluid p-4">
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <h1 style={{ color: colors.text }}>Einkauf</h1>
              <p style={{ color: colors.text }}>Hier können später Einkaufsfunktionen genutzt werden.</p>
            </div>
          </div>
        );
      case 'inventur':
        return (
          <div className="container-fluid p-4">
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <h1 style={{ color: colors.text }}>Inventur</h1>
              <p style={{ color: colors.text }}>Hier können später Inventurfunktionen genutzt werden.</p>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  const colors = getCurrentColors();

  return (
    <div style={{ backgroundColor: colors.background, minHeight: '100vh' }}>
      <style>
        {`
          .sidebar-icon {
            display: block !important;
            visibility: visible !important;
            opacity: 1 !important;
            font-size: 18px !important;
            font-weight: bold !important;
          }
          .sidebar-button {
            display: flex !important;
            align-items: center !important;
            justify-content: flex-start !important;
            min-height: 48px !important;
            width: 100% !important;
            margin: 0 !important;
            padding: 0 !important;
          }
          .sidebar-button span {
            display: block !important;
            visibility: visible !important;
            opacity: 1 !important;
          }
          .nav-link {
            margin: 0 !important;
            padding: 0 !important;
          }
          .btn-link {
            margin: 0 !important;
            padding: 0 !important;
          }
          .sidebar-sub-button {
            display: flex !important;
            align-items: center !important;
            justify-content: flex-start !important;
            width: 100% !important;
            margin: 0 !important;
            padding: 0 !important;
            border: none !important;
            background: none !important;
            color: inherit !important;
            text-decoration: none !important;
          }
        `}
      </style>

      {/* Header */}
      <nav className="navbar navbar-dark fixed-top" style={{ 
        backgroundColor: colors.primary,
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
        zIndex: 1030
      }}>
        <button className="btn btn-link text-white" onClick={toggleSidebar}>
          <span style={{ fontSize: '24px' }}>☰</span>
        </button>
                  <span className="navbar-brand mx-auto d-flex align-items-center">
            <span className="me-2" style={{ fontSize: '20px' }}>🍴</span>
            The Chef's Numbers
          </span>
        <button 
          className="btn btn-link text-white" 
          onClick={() => setShowDesignSelector(!showDesignSelector)}
          title="Design ändern"
        >
          <span style={{ fontSize: '20px' }}>🎨</span>
        </button>
      </nav>

      {/* Design Selector */}
      {showDesignSelector && (
        <div className="position-fixed top-0 start-0 w-100 h-100" style={{ 
          background: 'rgba(0,0,0,0.5)', 
          zIndex: 3000,
          top: 56
        }}>
          <div className="container mt-4">
            <div className="row justify-content-center">
              <div className="col-md-8">
                <div className="card" style={{ backgroundColor: colors.card }}>
                  <div className="card-header" style={{ backgroundColor: colors.secondary }}>
                    <h5 className="mb-0" style={{ color: colors.text }}>Design auswählen</h5>
                  </div>
                  <div className="card-body">
                    <div className="row">
                      {Object.entries(designTemplates).map(([key, template]) => (
                        <div key={key} className="col-md-6 mb-3">
                          <div 
                            className="card cursor-pointer" 
                            style={{ 
                              backgroundColor: template.colors.card,
                              border: currentDesign === key ? `3px solid ${template.colors.accent}` : `1px solid ${template.colors.cardBorder}`,
                              cursor: 'pointer'
                            }}
                            onClick={() => {
                              setCurrentDesign(key);
                              setShowDesignSelector(false);
                            }}
                          >
                            <div className="card-body">
                              <h6 className="card-title" style={{ color: template.colors.text }}>
                                {template.name}
                              </h6>
                              <p className="card-text small" style={{ color: template.colors.text }}>
                                {template.description}
                              </p>
                              <div className="d-flex gap-2">
                                <div style={{ 
                                  width: 20, 
                                  height: 20, 
                                  backgroundColor: template.colors.primary, 
                                  borderRadius: '50%' 
                                }}></div>
                                <div style={{ 
                                  width: 20, 
                                  height: 20, 
                                  backgroundColor: template.colors.accent, 
                                  borderRadius: '50%' 
                                }}></div>
                                <div style={{ 
                                  width: 20, 
                                  height: 20, 
                                  backgroundColor: template.colors.secondary, 
                                  borderRadius: '50%' 
                                }}></div>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                    <div className="text-center mt-3">
                      <button 
                        className="btn btn-secondary" 
                        onClick={() => setShowDesignSelector(false)}
                      >
                        Schließen
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Enhanced Sidebar */}
      <div
        className="position-fixed top-0 start-0 h-100 shadow transition-all"
        style={{ 
          width: sidebarOpen ? 224 : 60, 
          zIndex: 1020, 
          top: 56,
          transition: 'width 0.3s ease',
          backgroundColor: colors.sidebar,
          borderRight: `1px solid ${colors.cardBorder}`,
          display: isMobile ? (sidebarOpen ? 'block' : 'none') : 'block'
        }}
      >
        <div className="d-flex justify-content-between align-items-center p-3 border-bottom" style={{ borderColor: colors.cardBorder }}>
          {sidebarOpen && <span className="fw-bold" style={{ color: colors.text, fontSize: '1.1rem' }}>Navigation</span>}
          <button className="btn btn-link" onClick={toggleSidebar} style={{ color: colors.text }}>
            <span style={{ fontSize: '20px' }}>{sidebarOpen ? '✕' : '☰'}</span>
          </button>
        </div>

        <ul className="nav flex-column p-2">
          <li className="nav-item mb-2">
            <button 
              className="sidebar-button" 
              onClick={() => { setCurrentPage('dashboard'); if (isMobile) setSidebarOpen(false); }}
              title="Dashboard"
              style={{ 
                color: colors.text,
                borderRadius: '8px',
                backgroundColor: currentPage === 'dashboard' ? colors.accent + '20' : 'transparent',
                justifyContent: sidebarOpen ? 'flex-start' : 'center',
                minHeight: '50px',
                border: 'none',
                outline: 'none',
                padding: sidebarOpen ? '12px' : '2px',
                width: '100%'
              }}
            >
              <span className="sidebar-icon" style={{ 
                marginRight: sidebarOpen ? '12px' : '0',
                display: 'block',
                flexShrink: 0,
                fontSize: '18px',
                fontWeight: 'bold',
                color: colors.text,
                minWidth: '18px',
                textAlign: 'center',
                width: sidebarOpen ? 'auto' : '100%'
              }}>
                🧮
              </span>
              {sidebarOpen && <span>Dashboard</span>}
            </button>
            <ul className="nav flex-column mt-2" style={{ 
              marginLeft: sidebarOpen ? '1.25rem' : '0',
              paddingLeft: sidebarOpen ? '0' : '0'
            }}>
              <li>
                                  <button 
                    className="sidebar-sub-button" 
                    onClick={() => { setCurrentPage('rezepte'); if (isMobile) setSidebarOpen(false); }}
                  style={{ 
                    color: colors.text,
                    fontSize: '0.9rem',
                    borderRadius: '6px',
                    backgroundColor: currentPage === 'rezepte' ? colors.accent + '20' : 'transparent',
                    paddingLeft: sidebarOpen ? '1rem' : '0',
                    padding: sidebarOpen ? '8px 12px' : '3px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: sidebarOpen ? 'flex-start' : 'center',
                    minHeight: '32px',
                    width: '100%',
                    border: currentPage === 'rezepte' ? `2px solid ${colors.accent}` : 'none'
                  }}
                  title="Rezepte"
                >
                                      <span style={{ 
                      fontSize: '14px',
                      marginRight: sidebarOpen ? '8px' : '0',
                      width: sidebarOpen ? 'auto' : '100%',
                      textAlign: 'center'
                    }}>
                      📖
                    </span>
                  {sidebarOpen && <span>Rezepte</span>}
                </button>
              </li>
              <li>
                                  <button 
                    className="sidebar-sub-button" 
                    onClick={() => { setCurrentPage('artikel'); if (isMobile) setSidebarOpen(false); }}
                  style={{ 
                    color: colors.text,
                    fontSize: '0.9rem',
                    borderRadius: '6px',
                    backgroundColor: currentPage === 'artikel' ? colors.accent + '20' : 'transparent',
                    paddingLeft: sidebarOpen ? '1rem' : '0',
                    padding: sidebarOpen ? '8px 12px' : '3px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: sidebarOpen ? 'flex-start' : 'center',
                    minHeight: '32px',
                    width: '100%',
                    border: currentPage === 'artikel' ? `2px solid ${colors.accent}` : 'none'
                  }}
                  title="Artikel"
                >
                                      <span style={{ 
                      fontSize: '14px',
                      marginRight: sidebarOpen ? '8px' : '0',
                      width: sidebarOpen ? 'auto' : '100%',
                      textAlign: 'center'
                    }}>
                      📦
                    </span>
                  {sidebarOpen && <span>Artikel</span>}
                </button>
              </li>
              <li>
                                  <button 
                    className="sidebar-sub-button" 
                    onClick={() => { setCurrentPage('lieferanten'); if (isMobile) setSidebarOpen(false); }}
                  style={{ 
                    color: colors.text,
                    fontSize: '0.9rem',
                    borderRadius: '6px',
                    backgroundColor: currentPage === 'lieferanten' ? colors.accent + '20' : 'transparent',
                    paddingLeft: sidebarOpen ? '1rem' : '0',
                    padding: sidebarOpen ? '8px 12px' : '3px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: sidebarOpen ? 'flex-start' : 'center',
                    minHeight: '32px',
                    width: '100%'
                  }}
                  title="Lieferanten"
                >
                                      <span style={{ 
                      fontSize: '14px',
                      marginRight: sidebarOpen ? '8px' : '0',
                      width: sidebarOpen ? 'auto' : '100%',
                      textAlign: 'center'
                    }}>
                      👥
                    </span>
                  {sidebarOpen && <span>Lieferanten</span>}
                </button>
              </li>
            </ul>
          </li>
          <li className="nav-item mb-2">
            <button 
              className="sidebar-button" 
              onClick={() => { setCurrentPage('einkauf'); if (isMobile) setSidebarOpen(false); }}
              title="Einkauf"
              style={{ 
                color: colors.text,
                borderRadius: '8px',
                backgroundColor: currentPage === 'einkauf' ? colors.accent + '20' : 'transparent',
                justifyContent: sidebarOpen ? 'flex-start' : 'center',
                minHeight: '50px',
                border: 'none',
                outline: 'none',
                padding: sidebarOpen ? '12px' : '2px',
                width: '100%'
              }}
            >
              <span className="sidebar-icon" style={{ 
                marginRight: sidebarOpen ? '12px' : '0',
                display: 'block',
                flexShrink: 0,
                fontSize: '18px',
                fontWeight: 'bold',
                color: colors.text,
                minWidth: '18px',
                textAlign: 'center',
                width: sidebarOpen ? 'auto' : '100%'
              }}>
                🛒
              </span>
              {sidebarOpen && <span>Einkauf</span>}
            </button>
          </li>
          <li className="nav-item mb-2">
            <button 
              className="sidebar-button" 
              onClick={() => { setCurrentPage('inventur'); if (isMobile) setSidebarOpen(false); }}
              title="Inventur"
              style={{ 
                color: colors.text,
                borderRadius: '8px',
                backgroundColor: currentPage === 'inventur' ? colors.accent + '20' : 'transparent',
                justifyContent: sidebarOpen ? 'flex-start' : 'center',
                minHeight: '50px',
                border: 'none',
                outline: 'none',
                padding: sidebarOpen ? '12px' : '2px',
                width: '100%'
              }}
            >
              <span className="sidebar-icon" style={{ 
                marginRight: sidebarOpen ? '12px' : '0',
                display: 'block',
                flexShrink: 0,
                fontSize: '18px',
                fontWeight: 'bold',
                color: colors.text,
                minWidth: '18px',
                textAlign: 'center',
                width: sidebarOpen ? 'auto' : '100%'
              }}>
                📦
              </span>
              {sidebarOpen && <span>Inventur</span>}
            </button>
          </li>
        </ul>

        {/* Sidebar Footer */}
        {sidebarOpen && (
          <div className="position-absolute bottom-0 start-0 w-100 p-3 border-top" style={{ borderColor: colors.cardBorder }}>
            <button className="btn btn-link text-start w-100 d-flex align-items-center" style={{ color: colors.text }}>
              <span className="me-3" style={{ fontSize: '18px' }}>⚙️</span>
              Einstellungen
            </button>
          </div>
        )}
      </div>

      {/* Overlay for Mobile Sidebar */}
      {sidebarOpen && isMobile && (
        <div
          className="position-fixed top-0 start-0 w-100 h-100"
          style={{ background: 'rgba(0,0,0,0.3)', zIndex: 1000, top: 56 }}
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <div style={{ 
        marginLeft: isMobile ? 0 : (sidebarOpen ? 224 : 60), 
        marginTop: 56,
        transition: 'margin-left 0.3s ease'
      }}>
        {renderPage()}
      </div>
    </div>
  );
}

export default App;
