import React, { useState } from 'react';
import { FaUtensils, FaBars, FaTimes, FaCalculator, FaShoppingCart, FaBoxes } from 'react-icons/fa';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'dashboard':
        return (
          <div className="container mt-4">
            <h1>Dashboard</h1>
            <div className="row">
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Artikel</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Lieferanten</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Rezepte</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Gesamtwert</h5>
                    <p className="card-text">EUR 0,00</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      case 'artikel':
        return (
          <div className="container mt-4">
            <h1>Artikelverwaltung</h1>
            <p>Hier konnen spater Artikel verwaltet werden.</p>
          </div>
        );
      case 'lieferanten':
        return (
          <div className="container mt-4">
            <h1>Lieferantenverwaltung</h1>
            <p>Hier konnen spater Lieferanten verwaltet werden.</p>
          </div>
        );
      case 'rezepte':
        return (
          <div className="container mt-4">
            <h1>Rezeptverwaltung</h1>
            <p>Hier konnen spater Rezepte verwaltet werden.</p>
          </div>
        );
      case 'einkauf':
        return (
          <div className="container mt-4">
            <h1>Einkauf</h1>
            <p>Hier konnen spater Einkaufsfunktionen genutzt werden.</p>
          </div>
        );
      case 'inventur':
        return (
          <div className="container mt-4">
            <h1>Inventur</h1>
            <p>Hier konnen spater Inventurfunktionen genutzt werden.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      {/* Header */}
      <nav className="navbar navbar-dark bg-dark">
        <button className="btn btn-link text-white" onClick={toggleSidebar}>
          <FaBars size={24} />
        </button>
        <span className="navbar-brand mx-auto d-flex align-items-center">
          <FaUtensils className="me-2" />
          The Chef's Numbers
        </span>
      </nav>

      {/* Sidebar */}
      <div
        className={`position-fixed top-0 start-0 h-100 bg-light shadow ${sidebarOpen ? 'd-block' : 'd-none'}`}
        style={{ width: 250, zIndex: 2000 }}
      >
        <div className="d-flex justify-content-between align-items-center p-3 border-bottom">
          <span className="fw-bold">Menu</span>
          <button className="btn btn-link" onClick={toggleSidebar}>
            <FaTimes size={20} />
          </button>
        </div>
        <ul className="nav flex-column p-2">
          <li className="nav-item">
            <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('dashboard'); setSidebarOpen(false); }}>
              <FaCalculator className="me-2" /> Kalkulation
            </button>
            <ul className="nav flex-column ms-3">
              <li>
                <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('rezepte'); setSidebarOpen(false); }}>
                  Rezepte
                </button>
              </li>
              <li>
                <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('artikel'); setSidebarOpen(false); }}>
                  Artikel
                </button>
              </li>
              <li>
                <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('lieferanten'); setSidebarOpen(false); }}>
                  Lieferanten
                </button>
              </li>
            </ul>
          </li>
          <li className="nav-item">
            <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('einkauf'); setSidebarOpen(false); }}>
              <FaShoppingCart className="me-2" /> Einkauf
            </button>
          </li>
          <li className="nav-item">
            <button className="nav-link btn btn-link text-start" onClick={() => { setCurrentPage('inventur'); setSidebarOpen(false); }}>
              <FaBoxes className="me-2" /> Inventur
            </button>
          </li>
        </ul>
      </div>

      {/* Overlay for Sidebar */}
      {sidebarOpen && (
        <div
          className="position-fixed top-0 start-0 w-100 h-100"
          style={{ background: 'rgba(0,0,0,0.3)', zIndex: 1000 }}
          onClick={toggleSidebar}
        />
      )}

      {/* Main Content */}
      <div style={{ marginLeft: 0 }}>
        {renderPage()}
      </div>
    </div>
  );
}

export default App;
