import React, { useState, useEffect } from 'react';
import { FaUtensils, FaBars, FaTimes, FaCalculator, FaShoppingCart, FaBoxes, FaPalette, FaPlus, FaSearch, FaCog, FaUsers, FaTachometerAlt, FaEdit, FaTrash, FaList, FaTh, FaFilter, FaSort, FaPencilAlt, FaTimes as FaClose, FaSave, FaArrowLeft, FaPercent, FaEuroSign } from 'react-icons/fa';

// Design Templates
const designTemplates = {
  modern: {
    name: "Modern & Minimal",
    description: "Clean, viel Weißraum, moderne Typografie",
    colors: {
      primary: "#2c3e50",
      secondary: "#ecf0f1",
      accent: "#3498db",
      background: "#ffffff",
      sidebar: "#f8f9fa",
      text: "#2c3e50",
      card: "#ffffff",
      cardBorder: "#e9ecef",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(44, 62, 80, 0.1)"
    }
  },
  dark: {
    name: "Dark Theme",
    description: "Dunkles Design mit Akzentfarben",
    colors: {
      primary: "#1a1a1a",
      secondary: "#2d2d2d",
      accent: "#00d4aa",
      background: "#121212",
      sidebar: "#1e1e1e",
      text: "#ffffff",
      card: "#2d2d2d",
      cardBorder: "#404040",
      paper: "#2d2d2d",
      paperShadow: "0 4px 12px rgba(0, 0, 0, 0.3)"
    }
  },
  warm: {
    name: "Warm & Gastronomisch",
    description: "Braun/Beige Töne, gemütlich",
    colors: {
      primary: "#8B4513",
      secondary: "#F5DEB3",
      accent: "#D2691E",
      background: "#FFF8DC",
      sidebar: "#DEB887",
      text: "#654321",
      card: "#FDF5E6",
      cardBorder: "#D2B48C",
      paper: "#FFFFFF",
      paperShadow: "0 4px 12px rgba(139, 69, 19, 0.15)"
    }
  },
  professional: {
    name: "Professional Blue",
    description: "Business-Look mit Blau",
    colors: {
      primary: "#1e3a8a",
      secondary: "#dbeafe",
      accent: "#3b82f6",
      background: "#f8fafc",
      sidebar: "#1e40af",
      text: "#1e293b",
      card: "#ffffff",
      cardBorder: "#e2e8f0",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(30, 58, 138, 0.1)"
    }
  }
};

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  // Debug: Log currentPage changes
  useEffect(() => {
    console.log('Current page changed to:', currentPage);
  }, [currentPage]);
  const [currentDesign, setCurrentDesign] = useState('warm');
  const [showDesignSelector, setShowDesignSelector] = useState(false);

  // Artikelverwaltung State
  const [articles, setArticles] = useState([
    {
      id: '1',
      name: 'Tomaten',
      supplier: 'Gemüsehof Müller',
      category: 'Gemüse',
      bundlePrice: 12.50,
      bundleUnit: 'Kiste',
      content: 5,
      contentUnit: 'kg',
      pricePerUnit: 2.50,
      isGrossPrice: true
    },
    {
      id: '2',
      name: 'Olivenöl',
      supplier: 'Ölmühle Schmidt',
      category: 'Öle & Fette',
      bundlePrice: 45.00,
      bundleUnit: 'Flasche',
      content: 1,
      contentUnit: 'L',
      pricePerUnit: 45.00,
      isGrossPrice: false
    },
    {
      id: '3',
      name: 'Parmesan',
      supplier: 'Käserei Weber',
      category: 'Käse',
      bundlePrice: 28.80,
      bundleUnit: 'Stück',
      content: 0.5,
      contentUnit: 'kg',
      pricePerUnit: 57.60,
      isGrossPrice: true
    }
  ]);
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('list'); // 'list' oder 'grid'
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSupplier, setSelectedSupplier] = useState('');
  const [sortField, setSortField] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');
  const [selectedArticles, setSelectedArticles] = useState<string[]>([]);

  // Artikelformular State
  const [showArticleForm, setShowArticleForm] = useState(false);
  const [editingArticle, setEditingArticle] = useState<any>(null);
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
  const [categorySearchTerm, setCategorySearchTerm] = useState('');
  const [articleForm, setArticleForm] = useState({
    name: '',
    category: '',
    supplier: '',
    supplierArticleNumber: '',
    bundlePrice: 0,
    bundleUnit: '',
    content: 0,
    contentUnit: '',
    pricePerUnit: 0,
    isGrossPrice: true,
    ingredients: [] as string[],
    allergens: [] as string[],
    nutrition: {
      calories: 0,
      protein: 0,
      fat: 0,
      carbohydrates: 0,
      fiber: 0,
      sugar: 0,
      salt: 0
    }
  });

  // Check screen size on mount and resize
  useEffect(() => {
    const checkScreenSize = () => {
      setIsMobile(window.innerWidth < 768);
      if (window.innerWidth < 768) {
        setSidebarOpen(false);
      } else {
        setSidebarOpen(true);
      }
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getCurrentColors = () => designTemplates[currentDesign as keyof typeof designTemplates].colors;

  // Artikelverwaltung Hilfsfunktionen
  const getUniqueCategories = () => {
    const articleCategories = articles.map(article => article.category).filter(cat => cat);
    const allCategories = [...new Set([...CATEGORIES, ...articleCategories])];
    return allCategories.sort();
  };

  const getUniqueSuppliers = () => {
    const suppliers = articles.map(article => article.supplier);
    return Array.from(new Set(suppliers)).sort();
  };

  const filteredAndSortedArticles = () => {
    let filtered = articles.filter(article => 
      article.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === '' || article.category === selectedCategory) &&
      (selectedSupplier === '' || article.supplier === selectedSupplier)
    );

    // Sortierung
    filtered.sort((a, b) => {
      let aValue = a[sortField as keyof typeof a];
      let bValue = b[sortField as keyof typeof b];

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  };

