                    color: colors.text,
                    fontSize: '0.9rem',
                    borderRadius: '6px',
                    backgroundColor: currentPage === 'rezepte' ? colors.accent + '40' : 'transparent',
                    paddingLeft: sidebarOpen ? '1rem' : '0',
                    padding: sidebarOpen ? '8px 12px' : '3px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: sidebarOpen ? 'flex-start' : 'center',
                    minHeight: '32px',
                    width: '100%',
