                  <FaUtensils style={{ 
                    fontSize: '14px',
                    marginRight: sidebarOpen ? '8px' : '0',
                    width: sidebarOpen ? 'auto' : '100%',
                    textAlign: 'center'
                  }} />
                  {sidebarOpen && <span>Rezepte</span>}
                </button>
              </li>
              <li>
                <button 
                  className="sidebar-sub-button" 
                  onClick={() => { setCurrentPage('artikel'); if (isMobile) setSidebarOpen(false); }}
                  style={{ 
                    color: colors.text,
