            </button>
          </li>
        </ul>

        {/* Sidebar Footer */}
        {sidebarOpen && (
          <div className="position-absolute bottom-0 start-0 w-100 p-3 border-top" style={{ borderColor: colors.cardBorder }}>
            <button 
              style={{ 
                color: colors.text,
                background: 'none',
                border: 'none',
                width: '100%',
                textAlign: 'left',
                display: 'flex',
                alignItems: 'center',
                padding: '8px 12px',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '0.9rem'
              }}
