import React, { useState, useEffect } from 'react';
import { FaUtensils, FaBars, FaTimes, FaCalculator, FaShoppingCart, FaBoxes, FaPalette, FaPlus, FaSearch, FaCog, FaUsers, FaTachometerAlt, FaEdit, FaTrash, FaList, FaTh, FaFilter, FaSort, FaPencilAlt, FaTimes as FaClose, FaSave, FaArrowLeft, FaPercent, FaEuroSign } from 'react-icons/fa';

// Design Templates
const designTemplates = {
  modern: {
    name: "Modern & Minimal",
    description: "Clean, viel Weißraum, moderne Typografie",
    colors: {
      primary: "#2c3e50",
      secondary: "#ecf0f1",
      accent: "#3498db",
      background: "#ffffff",
      sidebar: "#f8f9fa",
      text: "#2c3e50",
      card: "#ffffff",
      cardBorder: "#e9ecef",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(44, 62, 80, 0.1)"
    }
  },
  dark: {
    name: "Dark Theme",
    description: "Dunkles Design mit Akzentfarben",
    colors: {
      primary: "#1a1a1a",
      secondary: "#2d2d2d",
      accent: "#00d4aa",
      background: "#121212",
      sidebar: "#1e1e1e",
      text: "#ffffff",
      card: "#2d2d2d",
      cardBorder: "#404040",
      paper: "#2d2d2d",
      paperShadow: "0 4px 12px rgba(0, 0, 0, 0.3)"
    }
  },
  warm: {
    name: "Warm & Gastronomisch",
    description: "Braun/Beige Töne, gemütlich",
    colors: {
      primary: "#8B4513",
      secondary: "#F5DEB3",
      accent: "#D2691E",
      background: "#FFF8DC",
      sidebar: "#DEB887",
      text: "#654321",
      card: "#FDF5E6",
      cardBorder: "#D2B48C",
      paper: "#FFFFFF",
      paperShadow: "0 4px 12px rgba(139, 69, 19, 0.15)"
    }
  },
  professional: {
    name: "Professional Blue",
    description: "Business-Look mit Blau",
    colors: {
      primary: "#1e3a8a",
      secondary: "#dbeafe",
      accent: "#3b82f6",
      background: "#f8fafc",
      sidebar: "#1e40af",
      text: "#1e293b",
      card: "#ffffff",
      cardBorder: "#e2e8f0",
      paper: "#ffffff",
      paperShadow: "0 4px 12px rgba(30, 58, 138, 0.1)"
    }
  }
};

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  // Debug: Log currentPage changes
  useEffect(() => {
    console.log('Current page changed to:', currentPage);
  }, [currentPage]);
  const [currentDesign, setCurrentDesign] = useState('warm');
  const [showDesignSelector, setShowDesignSelector] = useState(false);

  // Artikelverwaltung State
  const [articles, setArticles] = useState([
    {
      id: '1',
      name: 'Tomaten',
      supplier: 'Gemüsehof Müller',
      category: 'Gemüse',
      bundlePrice: 12.50,
      bundleUnit: 'Kiste',
      content: 5,
      contentUnit: 'kg',
      pricePerUnit: 2.50,
      isGrossPrice: true
    },
    {
      id: '2',
      name: 'Olivenöl',
      supplier: 'Ölmühle Schmidt',
      category: 'Öle & Fette',
      bundlePrice: 45.00,
      bundleUnit: 'Flasche',
      content: 1,
      contentUnit: 'L',
      pricePerUnit: 45.00,
      isGrossPrice: false
    },
    {
      id: '3',
      name: 'Parmesan',
      supplier: 'Käserei Weber',
      category: 'Käse',
      bundlePrice: 28.80,
      bundleUnit: 'Stück',
      content: 0.5,
      contentUnit: 'kg',
      pricePerUnit: 57.60,
      isGrossPrice: true
    }
  ]);
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('list'); // 'list' oder 'grid'
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedSupplier, setSelectedSupplier] = useState('');
  const [sortField, setSortField] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');
  const [selectedArticles, setSelectedArticles] = useState<string[]>([]);

  // Artikelformular State
  const [showArticleForm, setShowArticleForm] = useState(false);
  const [editingArticle, setEditingArticle] = useState<any>(null);
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);
  const [categorySearchTerm, setCategorySearchTerm] = useState('');
  const [articleForm, setArticleForm] = useState({
    name: '',
    category: '',
    supplier: '',
    supplierArticleNumber: '',
    bundlePrice: 0,
    bundleUnit: '',
    content: 0,
    contentUnit: '',
    pricePerUnit: 0,
    isGrossPrice: true,
    ingredients: [] as string[],
    allergens: [] as string[],
    nutrition: {
      calories: 0,
      protein: 0,
      fat: 0,
      carbohydrates: 0,
      fiber: 0,
      sugar: 0,
      salt: 0
    }
  });

  // Check screen size on mount and resize
  useEffect(() => {
    const checkScreenSize = () => {
      setIsMobile(window.innerWidth < 768);
      if (window.innerWidth < 768) {
        setSidebarOpen(false);
      } else {
        setSidebarOpen(true);
      }
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getCurrentColors = () => designTemplates[currentDesign as keyof typeof designTemplates].colors;

  // Artikelverwaltung Hilfsfunktionen
  const getUniqueCategories = () => {
    const categories = articles.map(article => article.category);
    return Array.from(new Set(categories)).sort();
  };

  const getUniqueSuppliers = () => {
    const suppliers = articles.map(article => article.supplier);
    return Array.from(new Set(suppliers)).sort();
  };

  const filteredAndSortedArticles = () => {
    let filtered = articles.filter(article => 
      article.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === '' || article.category === selectedCategory) &&
      (selectedSupplier === '' || article.supplier === selectedSupplier)
    );

    // Sortierung
    filtered.sort((a, b) => {
      let aValue = a[sortField as keyof typeof a];
      let bValue = b[sortField as keyof typeof b];

      if (typeof aValue === 'string' && typeof bValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });

    return filtered;
  };

  const handleSelectArticle = (articleId: string) => {
    setSelectedArticles(prev => 
      prev.includes(articleId) 
        ? prev.filter(id => id !== articleId)
        : [...prev, articleId]
    );
  };

  const handleSelectAll = () => {
    const filteredArticles = filteredAndSortedArticles();
    if (selectedArticles.length === filteredArticles.length) {
      setSelectedArticles([]);
    } else {
      setSelectedArticles(filteredArticles.map(article => article.id));
    }
  };

  const handleDeleteArticles = () => {
    if (selectedArticles.length > 0) {
      setArticles(prev => prev.filter(article => !selectedArticles.includes(article.id)));
      setSelectedArticles([]);
    }
  };

  const formatPrice = (price: number) => {
    return `€${price.toFixed(2)}`;
  };

  // Konstanten für Artikelformular
  const UNITS = [
    'kg', 'g', 'l', 'ml', 'Stück', 'Packung', 'Kiste', 'Karton', 'Flasche', 'Dose', 
    'Beutel', 'Schachtel', 'Tube', 'Glas', 'Becher', 'Tüte', 'Rolle', 'Meter', 'cm', 'mm'
  ];

  const INGREDIENTS = [
    'Weizen', 'Roggen', 'Gerste', 'Hafer', 'Dinkel', 'Emmer', 'Einkorn', 'Khorasan-Weizen',
    'Milch', 'Laktose', 'Kasein', 'Molke', 'Butter', 'Sahne', 'Joghurt', 'Käse',
    'Ei', 'Eiklar', 'Eigelb',
    'Fisch', 'Lachs', 'Thunfisch', 'Kabeljau', 'Forelle', 'Sardinen', 'Makrele',
    'Krebstiere', 'Garnelen', 'Krabben', 'Hummer', 'Langusten',
    'Weichtiere', 'Muscheln', 'Austern', 'Miesmuscheln', 'Jakobsmuscheln',
    'Erdnüsse', 'Mandeln', 'Haselnüsse', 'Walnüsse', 'Cashewnüsse', 'Pistazien', 'Macadamianüsse', 'Pekannüsse',
    'Soja', 'Sojabohnen', 'Sojamilch', 'Tofu', 'Tempeh', 'Miso',
    'Sesam', 'Sesamsamen', 'Sesamöl', 'Tahini',
    'Senf', 'Senfkörner', 'Senfpulver',
    'Sellerie', 'Staudensellerie', 'Knollensellerie',
    'Lupinen', 'Lupinenmehl', 'Lupinenprotein',
    'Schwefeldioxid', 'Sulfite', 'Sulfat',
    'Gluten', 'Weizengluten', 'Roggengluten',
    'Gelatine', 'Agar-Agar', 'Pektin',
    'Hefe', 'Backhefe', 'Bierhefe',
    'Konservierungsstoffe', 'Antioxidantien', 'Farbstoffe', 'Aromen'
  ];

  const ALLERGENS = [
    'A - Glutenhaltige Getreide', 'B - Krebstiere', 'C - Eier', 'D - Fische',
    'E - Erdnüsse', 'F - Sojabohnen', 'G - Milch', 'H - Schalenfrüchte',
    'I - Sellerie', 'J - Senf', 'K - Sesamsamen', 'L - Schwefeldioxid/Sulfite',
    'M - Lupinen', 'N - Weichtiere'
  ];

  // Artikelformular Hilfsfunktionen
  const calculatePricePerUnit = (bundlePrice: number, content: number, isGross: boolean) => {
    if (content <= 0) return 0;
    const netPrice = isGross ? bundlePrice / 1.19 : bundlePrice; // 19% MwSt
    return netPrice / content;
  };

  const handlePriceChange = (newBundlePrice: number, isGross: boolean) => {
    const newPricePerUnit = calculatePricePerUnit(newBundlePrice, articleForm.content, isGross);
    setArticleForm(prev => ({
      ...prev,
      bundlePrice: newBundlePrice,
      isGrossPrice: isGross,
      pricePerUnit: newPricePerUnit
    }));
  };

  const handleContentChange = (newContent: number) => {
    const newPricePerUnit = calculatePricePerUnit(articleForm.bundlePrice, newContent, articleForm.isGrossPrice);
    setArticleForm(prev => ({
      ...prev,
      content: newContent,
      pricePerUnit: newPricePerUnit
    }));
  };

  const handleIngredientToggle = (ingredient: string) => {
    setArticleForm(prev => ({
      ...prev,
      ingredients: prev.ingredients.includes(ingredient)
        ? prev.ingredients.filter(i => i !== ingredient)
        : [...prev.ingredients, ingredient]
    }));
  };

  const handleAllergenToggle = (allergen: string) => {
    setArticleForm(prev => ({
      ...prev,
      allergens: prev.allergens.includes(allergen)
        ? prev.allergens.filter(a => a !== allergen)
        : [...prev.allergens, allergen]
    }));
  };

  const resetArticleForm = () => {
    setArticleForm({
      name: '',
      category: '',
      supplier: '',
      supplierArticleNumber: '',
      bundlePrice: 0,
      bundleUnit: '',
      content: 0,
      contentUnit: '',
      pricePerUnit: 0,
      isGrossPrice: true,
      ingredients: [],
      allergens: [],
      nutrition: {
        calories: 0,
        protein: 0,
        fat: 0,
        carbohydrates: 0,
        fiber: 0,
        sugar: 0,
        salt: 0
      }
    });
    setEditingArticle(null);
  };

  const handleSaveArticle = () => {
    if (editingArticle) {
      // Artikel bearbeiten
      setArticles(prev => prev.map(article => 
        article.id === editingArticle.id 
          ? { ...articleForm, id: article.id }
          : article
      ));
    } else {
      // Neuen Artikel erstellen
      const newArticle = {
        ...articleForm,
        id: Date.now().toString()
      };
      setArticles(prev => [...prev, newArticle]);
    }
    setShowArticleForm(false);
    resetArticleForm();
  };

  const handleEditArticle = (article: any) => {
    setEditingArticle(article);
    setArticleForm({
      name: article.name,
      category: article.category,
      supplier: article.supplier,
      supplierArticleNumber: article.supplierArticleNumber || '',
      bundlePrice: article.bundlePrice,
      bundleUnit: article.bundleUnit,
      content: article.content,
      contentUnit: article.contentUnit,
      pricePerUnit: article.pricePerUnit,
      isGrossPrice: article.isGrossPrice,
      ingredients: article.ingredients || [],
      allergens: article.allergens || [],
      nutrition: article.nutrition || {
        calories: 0, protein: 0, fat: 0, carbohydrates: 0, fiber: 0, sugar: 0, salt: 0
      }
    });
    setShowArticleForm(true);
  };

  const renderPage = () => {
    const colors = getCurrentColors();

    switch (currentPage) {
      case 'dashboard':
        return (
          <div className="container-fluid p-4">
            {/* Paper-like Dashboard */}
            <div style={{
              backgroundColor: colors.paper || colors.card,
              borderRadius: '12px',
              boxShadow: colors.paperShadow || '0 4px 12px rgba(0,0,0,0.1)',
              padding: '2rem',
              minHeight: 'calc(100vh - 120px)',
              border: `1px solid ${colors.cardBorder}`
            }}>
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h1 style={{ color: colors.text, margin: 0 }}>Dashboard</h1>
                <div className="d-flex gap-2">
                  <button className="btn btn-outline-secondary btn-sm">
                    <FaPlus className="me-1" />
                    Neuer Artikel
                  </button>
                  <button className="btn btn-outline-secondary btn-sm">
                    <FaPlus className="me-1" />
                    Neues Rezept
                  </button>
                </div>
              </div>

              <div className="row">
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        📦
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Artikel</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Verfügbare Artikel</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        👥
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Lieferanten</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Aktive Partner</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        📖
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Rezepte</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>0</p>
                      <small style={{ color: colors.text }}>Gespeicherte Rezepte</small>
                    </div>
                  </div>
                </div>
                <div className="col-md-3 mb-4">
                  <div className="card h-100" style={{ 
                    backgroundColor: colors.card, 
                    borderColor: colors.cardBorder,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
                    borderRadius: '8px'
                  }}>
                    <div className="card-body text-center">
                      <div style={{ 
                        width: 60, 
                        height: 60, 
                        backgroundColor: colors.accent, 
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        margin: '0 auto 1rem',
                        color: 'white',
                        fontSize: '1.5rem'
                      }}>
                        💰
                      </div>
                      <h5 className="card-title" style={{ color: colors.text }}>Gesamtwert</h5>
                      <p className="card-text" style={{ color: colors.accent, fontSize: '2.5rem', fontWeight: 'bold', margin: 0 }}>€0</p>
                      <small style={{ color: colors.text }}>Lagerwert</small>
                    </div>
                  </div>
                </div>
              </div>

              {/* Quick Actions Section */}
              <div className="row mt-4">
                <div className="col-12">
                  <h3 style={{ color: colors.text, marginBottom: '1rem' }}>Schnellzugriff</h3>
                  <div className="row">
                    <div className="col-md-4 mb-3">
                      <button className="btn btn-outline-primary w-100 p-3" style={{ 
                        borderColor: colors.accent, 
                        color: colors.accent,
                        borderRadius: '8px'
                      }}>
                        <FaPlus className="me-2" />
                        Neuen Artikel anlegen
                      </button>
                    </div>
