import React, { useState, useEffect } from 'react';
import { FaUtensils, FaBars, FaTimes, FaCalculator, FaShoppingCart, FaBoxes } from 'react-icons/fa';

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(false);
  const [currentPage, setCurrentPage] = useState('dashboard');

  // Check screen size on mount and resize
  useEffect(() => {
    const checkScreenSize = () => {
      setIsMobile(window.innerWidth < 768);
      if (window.innerWidth < 768) {
        setSidebarOpen(false);
      } else {
        setSidebarOpen(true);
      }
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'dashboard':
        return (
          <div className="container-fluid mt-4">
            <h1>Dashboard</h1>
            <div className="row">
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Artikel</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Lieferanten</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Rezepte</h5>
                    <p className="card-text">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-3 mb-3">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">Gesamtwert</h5>
                    <p className="card-text">EUR 0,00</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      case 'artikel':
        return (
          <div className="container-fluid mt-4">
            <h1>Artikelverwaltung</h1>
            <p>Hier können später Artikel verwaltet werden.</p>
          </div>
        );
      case 'lieferanten':
        return (
          <div className="container-fluid mt-4">
            <h1>Lieferantenverwaltung</h1>
            <p>Hier können später Lieferanten verwaltet werden.</p>
          </div>
        );
      case 'rezepte':
        return (
          <div className="container-fluid mt-4">
            <h1>Rezeptverwaltung</h1>
            <p>Hier können später Rezepte verwaltet werden.</p>
          </div>
        );
      case 'einkauf':
        return (
          <div className="container-fluid mt-4">
            <h1>Einkauf</h1>
            <p>Hier können später Einkaufsfunktionen genutzt werden.</p>
          </div>
        );
      case 'inventur':
        return (
          <div className="container-fluid mt-4">
            <h1>Inventur</h1>
            <p>Hier können später Inventurfunktionen genutzt werden.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      {/* Header */}
      <nav className="navbar navbar-dark bg-dark fixed-top">
        <button className="btn btn-link text-white" onClick={toggleSidebar}>
          <FaBars size={24} />
        </button>
        <span className="navbar-brand mx-auto d-flex align-items-center">
          <FaUtensils className="me-2" />
          The Chef's Numbers
        </span>
      </nav>

      {/* Sidebar */}
      <div
        className={`position-fixed top-0 start-0 h-100 bg-light shadow transition-all ${
          sidebarOpen ? 'd-block' : 'd-none'
        } ${isMobile ? '' : 'd-block'}`}
        style={{ 
          width: sidebarOpen ? 250 : 60, 
          zIndex: 2000, 
          top: 56,
          transition: 'width 0.3s ease'
        }}
      >
        <div className="d-flex justify-content-between align-items-center p-3 border-bottom">
          {sidebarOpen && <span className="fw-bold">Menu</span>}
          <button className="btn btn-link" onClick={toggleSidebar}>
            {sidebarOpen ? <FaTimes size={20} /> : <FaBars size={20} />}
          </button>
        </div>

        <ul className="nav flex-column p-2">
          <li className="nav-item">
            <button 
              className="nav-link btn btn-link text-start d-flex align-items-center" 
              onClick={() => { setCurrentPage('dashboard'); if (isMobile) setSidebarOpen(false); }}
              title="Kalkulation"
            >
              <FaCalculator className="me-2" />
              {sidebarOpen && <span>Kalkulation</span>}
            </button>
            {sidebarOpen && (
              <ul className="nav flex-column ms-3">
                <li>
                  <button 
                    className="nav-link btn btn-link text-start" 
                    onClick={() => { setCurrentPage('rezepte'); if (isMobile) setSidebarOpen(false); }}
                  >
                    Rezepte
                  </button>
                </li>
                <li>
                  <button 
                    className="nav-link btn btn-link text-start" 
                    onClick={() => { setCurrentPage('artikel'); if (isMobile) setSidebarOpen(false); }}
                  >
                    Artikel
                  </button>
                </li>
                <li>
                  <button 
                    className="nav-link btn btn-link text-start" 
                    onClick={() => { setCurrentPage('lieferanten'); if (isMobile) setSidebarOpen(false); }}
                  >
                    Lieferanten
                  </button>
                </li>
              </ul>
            )}
          </li>
          <li className="nav-item">
            <button 
              className="nav-link btn btn-link text-start d-flex align-items-center" 
              onClick={() => { setCurrentPage('einkauf'); if (isMobile) setSidebarOpen(false); }}
              title="Einkauf"
            >
              <FaShoppingCart className="me-2" />
              {sidebarOpen && <span>Einkauf</span>}
            </button>
          </li>
          <li className="nav-item">
            <button 
              className="nav-link btn btn-link text-start d-flex align-items-center" 
              onClick={() => { setCurrentPage('inventur'); if (isMobile) setSidebarOpen(false); }}
              title="Inventur"
            >
              <FaBoxes className="me-2" />
              {sidebarOpen && <span>Inventur</span>}
            </button>
          </li>
        </ul>
      </div>

      {/* Overlay for Mobile Sidebar */}
      {sidebarOpen && isMobile && (
        <div
          className="position-fixed top-0 start-0 w-100 h-100"
          style={{ background: 'rgba(0,0,0,0.3)', zIndex: 1000, top: 56 }}
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content */}
      <div style={{ 
        marginLeft: isMobile ? 0 : (sidebarOpen ? 250 : 60), 
        marginTop: 56,
        transition: 'margin-left 0.3s ease'
      }}>
        {renderPage()}
      </div>
    </div>
  );
}

export default App;
